Import-Csv .\ListUsersAD.csv | foreach-object { 
New-ADUser ` -SamAccountName $_.SamAccountName ` -UserPrincipalName $_.userPrincipalName ` -Name $_.name ` -DisplayName $_.name ` -GivenName $_.GivenName ` -SurName $_.SurName ` -Title $_.Title ` -Company $_.Company ` -Department $_.Department ` -EmailAddress $_.EmailAddress ` -Description $_.Description ` -Country $_.Country ` -Path "OU=Usuarios,DC=CONTOSO,DC=local" ` -AccountPassword (ConvertTo-SecureString "P@ssword" -AsPlainText -force) ` -Enabled $True ` -PasswordNeverExpires $False ` -ChangePasswordAtLogon $True -PassThru }